details.description:
{# display HouseNo input before Street input #}
{% $_HouseNoFirst = false %}

{# get default country id (required for showing mandatory fields depending on country) #}
{%
	$_defaultCountryId = "";
	for ( $_country in GetCheckoutShippingCountriesList() )
	{
		if ( $_defaultCountryId == "" )
		{
			$_defaultCountryId = $_country->ShippingCountryID;
		}
	}
%}

{% $_customer = GetCheckoutCustomerInvoiceAddress() %}

<div class="container checkout margin-top-1">

	<form data-plenty-checkout-form="customerRegistration" data-plenty="submit:Validator.validate(this, 'has-error')" onsubmit="plenty.AuthenticationService.registerCustomer(); return false;" action="{% if ( $BasketItemQuantity > 0 ) { Link_Checkout(3); } else { Link_Home(); } %}"{% if GetSystemSetting('checkout.addressdoctor.registration_validator_active') == 1 %}  data-plenty-address-doctor="Street, ZIP, City"{% endif %}>

		<div class="panel panel-default">
			<div class="panel-heading">
				<h3 class="panel-title">Rechnungsdaten</h3>
			</div>
			<div class="panel-body padding-bottom-1">

				<div class="hr-text margin-top-1 margin-bottom-05 muted">
					<span>Kundendaten</span>
				</div>

				<div class="row">
					<div class="col-xs-12 col-sm-6">
						<div class="form-group" data-plenty-validate="mail">
							<label for="registrationEmail">E-Mail-Adresse</label>
							<input class="form-control" id="registrationEmail" type="text" placeholder="E-Mail-Adresse" name="Email" value="$_customer->Email">
						</div>
					</div>
					<div class="col-xs-12 col-sm-6">
						<div class="form-group" data-plenty-validate="value" data-plenty-validation-value="#registrationEmail">
							<label for="registrationEmailRepeat">E-Mail-Adresse wiederholen</label>
							<input class="form-control" id="registrationEmailRepeat" type="text" placeholder="E-Mail-Adresse wiederholen" name="EmailRepeat" value="$_customer->Email">
						</div>
					</div>
				</div>


				<div class="row">
					<div class="col-xs-12 col-sm-6" data-plenty-checkout-registration>
						<div class="form-group" data-plenty-validate="text">
							<label for="registrationPassword">Passwort</label>
							<input class="form-control" id="registrationPassword" type="password" placeholder="Passwort" name="Password" value="$_customer->Password">
						</div>
					</div>
					<div class="col-xs-12 col-sm-6" data-plenty-checkout-registration>
						<div class="form-group" data-plenty-validate="value" data-plenty-validation-value="#registrationPassword">
							<label for="registrationPasswordRepeat">Passwort wiederholen</label>
							<input class="form-control" id="registrationPasswordRepeat" type="password" placeholder="Passwort wiederholen" name="PasswordRepeat" value="$_customer->Password">
						</div>
					</div>
				</div>

				<div class="row">
					{% if GetGlobal("ValidateInvoiceAddressCompanyName") == true %}
						{# b2b shop #}
						<div class="col-xs-12 col-sm-6">
							<div class="form-group"{% if GetGlobal("ValidateInvoiceAddressVatIfCompany") == true %} data-plenty-validate="text"{% endif %}>
								<label for="registrationVatNumber">USt-IdNr.</label>
								<input class="form-control" id="registrationVatNumber" type="text" placeholder="USt-IdNr." name="VATNumber" value="$_customer->VATNumber">
							</div>
						</div>
					{% else %}
						{# not b2b #}
						<div class="col-xs-12 col-sm-6">
							<div class="form-group"{% if GetGlobal("ValidateInvoiceAddressBirthDate") == true %} data-plenty-validate="number, number, number"{% endif %}>
								<label for="registrationBirthDay">Geburtsdatum (Tag<span class="sr-only">)</span></label>
								<label class="separate" for="registrationBirthMonth"><span class="sr-only">Geburtsdatum (</span>Monat<span class="sr-only">)</span></label>
								<label class="separate" for="registrationBirthYear"><span class="sr-only">Geburtsdatum (</span>Jahr)</label>
								<div class="form-inline form-block-3">
									<input class="form-control width-25" id="registrationBirthDay" type="text" placeholder="TT" name="BirthDay" maxlength="2" value="$_customer->BirthDay"><!--
									--><input class="form-control width-25" id="registrationBirthMonth" type="text" placeholder="MM" name="BirthMonth" maxlength="2" value="$_customer->BirthMonth"><!--
									--><input class="form-control width-50" id="registrationBirthYear" type="text" placeholder="JJJJ" name="BirthYear" maxlength="4" value="$_customer->BirthYear">
								</div>
							</div>
						</div>
					{% endif %}
					<div class="col-xs-12 col-sm-6">
						<div class="form-group"{% if GetGlobal("ValidateInvoiceAddressPhoneNumber") == true || ( GetGlobal("ValidateInvoiceAddressPhoneNumberIfForeignCustomer") == true && $StoreCountryID != $_defaultCountryId ) %} data-plenty-validate="text"{% endif %}>
							<label for="registrationPhone">Telefon</label>
							<input class="form-control" id="registrationPhone" type="text" placeholder="Telefon" name="PhoneNumber" value="$_customer->PhoneNumber">
						</div>
					</div>
				</div>

				<div class="hr-text margin-bottom-05 muted">
					<span>Rechnungsanschrift</span>
				</div>

				<div class="row">
					<div class="col-xs-12 col-sm-6">
						<div class="form-group">
							<label for="registrationFormOfAddress">Anrede</label>
							<select class="form-control" id="registrationFormOfAddress" name="FormOfAddressID">
								<option value="0">Herr</option>
								<option value="1"{% if $_customer->FormOfAddressID == 1 %} selected{% endif %}>Frau</option>
								<option value="2"{% if GetGlobal("ValidateInvoiceAddressCompanyName") == true || $_customer->FormOfAddressID == 2 %} selected{% endif %}>Firma</option>
								<option value="3"{% if $_customer->FormOfAddressID == 3 %} selected{% endif %}>Familie</option>
							</select>
						</div>

						<div class="form-group{% if GetGlobal("ValidateInvoiceAddressCompanyName") == false %} disabled{% endif %}"{% if GetGlobal("ValidateInvoiceAddressCompanyName") == true %} data-plenty-validate="text"{% endif %}>
							<label for="registrationCompany">Firma</label>
							<input class="form-control" id="registrationCompany" type="text" placeholder="Firma" name="Company"{% if GetGlobal("ValidateInvoiceAddressCompanyName") == false %} disabled{% endif %} value="$_customer->Company">
						</div>

						<div class="form-group"{% if ( 
								( ( $_customer->FormOfAddressID == 0 || $_customer->FormOfAddressID == 1 ) && GetGlobal("ValidateInvoiceAddressFirstName") == true )
								|| ( $_customer->FormOfAddressID == 2 && GetGlobal("ValidateInvoiceAddressFirstNameIfCompany") == true )
								|| ( $_customer->FormOfAddressID == 3 && GetGlobal("ValidateInvoiceAddressFirstNameIfFamily") == true )
							) %} data-plenty-validate="text"{% endif %}>
							<label for="registrationFirstName">Vorname</label>
							<input class="form-control" id="registrationFirstName" type="text" placeholder="Vorname" name="FirstName" value="$_customer->FirstName">
						</div>

						<div class="form-group"{% if ( 
								( $_customer->FormOfAddressID != 2 && GetGlobal("ValidateInvoiceAddressLastName") == true ) 
								|| ( $_customer->FormOfAddressID == 2 && GetGlobal("ValidateInvoiceAddressLastNameIfCompany") == true )
							) %} data-plenty-validate="text"{% endif %}>
							<label for="registrationLastName">Nachname</label>
							<input class="form-control" id="registrationLastName" type="text" placeholder="Nachname" name="LastName" value="$_customer->LastName">
						</div>
					</div>
					<div class="col-xs-12 col-sm-6">
						<div class="form-group">
							<label for="registrationAdditional">Adresszusatz</label>
							<input class="form-control" id="registrationAdditional" type="text" placeholder="Adresszusatz (z.B. c/o)" name="AddressAdditional" value="$_customer->AddressAdditional">
						</div>

						{% if $_HouseNoFirst %}

							{%
								$_validate = false;
								$_validationString = "";
								if ( GetGlobal("ValidateInvoiceAddressHouseNumber") == true )
								{
									$_validate = true;
									$_validationString .= "text";
								}
								else
								{
									$_validationString .= "none";
								}
								if ( GetGlobal("ValidateInvoiceAddressStreet") == true )
								{
									$_validate = true;
									$_validationString .= "text, ";
								}
								else
								{
									$_validationString .= "none, ";
								}
							%}
							<div class="form-group"{% if $_validate == true %} data-plenty-validate="$_validationString"{% endif %}>
								<label for="registrationNumber">Hausnr.</label><label for="registrationStreet">Straße</label>
								<div class="form-inline form-block-2">
									<input class="form-control width-25" id="registrationNumber" type="text" placeholder="Nr." name="HouseNo" value="$_customer->HouseNo"><!--
									--><input class="form-control width-75" id="registrationStreet" type="text" placeholder="Straße" name="Street" value="$_customer->Street">
								</div>
							</div>

						{% else %}

							{%
								$_validate = false;
								$_validationString = "";
								if ( GetGlobal("ValidateInvoiceAddressStreet") == true )
								{
									$_validate = true;
									$_validationString .= "text, ";
								}
								else
								{
									$_validationString .= "none, ";
								}
								if ( GetGlobal("ValidateInvoiceAddressHouseNumber") == true )
								{
									$_validate = true;
									$_validationString .= "text";
								}
								else
								{
									$_validationString .= "none";
								}
							%}
							<div class="form-group"{% if $_validate == true %} data-plenty-validate="$_validationString"{% endif %}>
								<label for="registrationStreet">Straße</label><label for="registrationNumber">Hausnr.</label>
								<div class="form-inline form-block-2">
									<input id="registrationStreet" class="form-control width-75" type="text" placeholder="Straße" name="Street" value="$_customer->Street"><!--
									--><input class="form-control width-25" id="registrationNumber" type="text" placeholder="Nr." name="HouseNo" value="$_customer->HouseNo">
								</div>
							</div>

						{% endif %}

						{%
							$_validate = false;
							$_validationString = "";
							if ( GetGlobal("ValidateInvoiceAddressZip") == true )
							{
								$_validate = true;
								$_validationString .= "text, ";
							}
							else
							{
								$_validationString .= "none, ";
							}
							if ( GetGlobal("ValidateInvoiceAddressCity") == true )
							{
								$_validate = true;
								$_validationString .= "text";
							}
							else
							{
								$_validationString .= "none";
							}
						%}
						<div class="form-group"{% if $_validate == true %} data-plenty-validate="$_validationString"{% endif %}>
							<label for="registrationZip">PLZ</label><label for="registrationCity">Ort</label>
							<div class="form-inline form-block-2">
								<input class="form-control width-25" id="registrationZip" type="text" placeholder="PLZ" name="ZIP" value="$_customer->ZIP"><!--
								--><input class="form-control width-75" id="registrationCity" type="text" placeholder="Ort" name="City" value="$_customer->City">
							</div>
						</div>

						{# inject country select field with optional stats select field. #}
						{% SetGlobal("ShippingCountriesListConfig", "registration;" . $_customer->CountryID . ";" . $_customer->StateID); %}
						{% Container_CheckoutShippingCountriesList() %}
					</div>
				</div>

				{%
					{# check for user-selected settings (e.g. after refresh while registering as guest) making optional fields mandatory  #}
					$_additionalFieldsVisible = false;
					if (
						( $_customer->FormOfAddressID == 2 && GetGlobal("ValidateInvoiceAddressVatIfCompany") == true )
						|| ( GetGlobal("ValidateInvoiceAddressFaxNumber") == true || ( GetGlobal("ValidateInvoiceAddressFaxNumberIfForeignCustomer") == true && $StoreCountryID != $_defaultCountryId ) )
						|| ( GetGlobal("ValidateInvoiceAddressMobileNumber") == true || ( GetGlobal("ValidateInvoiceAddressMobileNumberIfForeignCustomer") == true && $StoreCountryID != $_defaultCountryId ) )
						|| ( GetGlobal("ValidateInvoiceAddressCompanyName") == true && GetGlobal("ValidateInvoiceAddressBirthDate") == true )
						|| $_customer->FaxNumber != ""
						|| $_customer->MobileNumber != ""
						|| $_customer->Postnummer != ""
						|| ( GetGlobal("ValidateInvoiceAddressCompanyName") == false && $_customer->VATNumber != "" )
						|| ( GetGlobal("ValidateInvoiceAddressCompanyName") == true && ( $_customer->BirthDay != "" || $_customer->BirthMonth != "" || $_customer->BirthYear != "" ) )
					) 
					{
						$_additionalFieldsVisible = true;
					}
				%}

				<div class="hr-text margin-top-05">
					<span><a class="btn btn-default{% if $_additionalFieldsVisible == true %} active disabled{% endif %}" data-plenty="UI.initSlideToggle(this)" data-plenty-rel="#additionalInvoiceFields" href="javascript:void(0);">Optionale Felder</a></span>
				</div>

				<div id="additionalInvoiceFields"{% if $_additionalFieldsVisible == false %} style="display: none;"{% endif %}>
					<div class="row margin-top-1"{% if GetGlobal("ValidateInvoiceAddressFaxNumber") == true || ( GetGlobal("ValidateInvoiceAddressFaxNumberIfForeignCustomer") == true && $StoreCountryID != $_defaultCountryId ) %} data-plenty-validate="text"{% endif %}>
						<div class="col-xs-12 col-sm-6">
							<div class="form-group">
								<label for="registrationFax">Fax</label>
								<input class="form-control" id="registrationFax" type="text" placeholder="Fax" name="FaxNumber" value="$_customer->FaxNumber">
							</div>
						</div>

						<div class="col-xs-12 col-sm-6">
							<div class="form-group"{% if GetGlobal("ValidateInvoiceAddressMobileNumber") == true || ( GetGlobal("ValidateInvoiceAddressMobileNumberIfForeignCustomer") == true && $StoreCountryID != $_defaultCountryId ) %} data-plenty-validate="text"{% endif %}>
								<label for="registrationMobile">Mobile</label>
								<input class="form-control" id="registrationMobile" type="text" placeholder="Mobile" name="MobileNumber" value="$_customer->MobileNumber">
							</div>
						</div>

						{% if GetSystemSetting('checkout.dhl.allow_postident') == 1 %}
							<div class="col-xs-12 col-sm-6">
								<div class="form-group">
									<label for="registrationPostNumber">Postnummer</label>
									<input class="form-control" id="registrationPostNumber" type="text" placeholder="Postnummer" name="Postnummer" value="$_customer->Postnummer">
								</div>
							</div>
						{% endif %}

						{% if GetGlobal("ValidateInvoiceAddressCompanyName") == false %}
							{# b2b shop #}
							<div class="col-xs-12 col-sm-6">
								<div class="form-group"{% if $_customer->FormOfAddressID == 2 && GetGlobal("ValidateInvoiceAddressVatIfCompany") == true %} data-plenty-validate="text"{% endif %}>
									<label for="registrationVatNumber">USt-IdNr.</label>
									<input class="form-control" id="registrationVatNumber" type="text" placeholder="USt-IdNr." name="VATNumber" value="$_customer->VATNumber">
								</div>
							</div>
						{% else %}
							{# not b2b #}
							<div class="col-xs-12 col-sm-6">
								<div class="form-group"{% if GetGlobal("ValidateInvoiceAddressBirthDate") == true %} data-plenty-validate="number, number, number"{% endif %}>
									<label for="registrationBirthDay">Geburtsdatum (Tag<span class="sr-only">)</span></label>
									<label class="separate" for="registrationBirthMonth"><span class="sr-only">Geburtsdatum (</span>Monat<span class="sr-only">)</span></label>
									<label class="separate" for="registrationBirthYear"><span class="sr-only">Geburtsdatum (</span>Jahr)</label>
									<div class="form-inline form-block-3">
										<input class="form-control width-25" id="registrationBirthDay" type="text" placeholder="TT" name="BirthDay" maxlength="2" value="$_customer->BirthDay"><!--
										--><input class="form-control width-25" id="registrationBirthMonth" type="text" placeholder="MM" name="BirthMonth" maxlength="2" value="$_customer->BirthMonth"><!--
										--><input class="form-control width-50" id="registrationBirthYear" type="text" placeholder="JJJJ" name="BirthYear" maxlength="4" value="$_customer->BirthYear">
									</div>
								</div>
							</div>
						{% endif %}

					</div>
				</div>
			</div>
		</div>

		{# customer properties #}
		{%
			$_propertiesAvailable = false;
			for ( $_check in GetCheckoutCustomerPropertiesList() ) {
				$_propertiesAvailable = true;
				$LoopBreak[1];
			}
		%}

		{# only show if customer properties #}
		{% if $_propertiesAvailable %}
			{# show CustomerProperty #}
			<div class="panel panel-default">

				<div class="panel-heading">
					<h3 class="panel-title">{% if $Lang=="de" %}Kundenumfrage{% else %}Customer survey{% endif %}</h3>
				</div>

				<div class="panel-body padding-bottom-1">
					<div class="row margin-top-1">
						{% for $_customerProperty in GetCheckoutCustomerPropertiesList() %}
							<div class="col-xs-12 col-sm-6">
								<div class="form-group"{% if ( $_customerProperty->PropertyIsMandatory ) %} data-plenty-validate="text"{% endif %}>
									<label for="plentyCustomerProperty-$_customerProperty->PropertyID">$_customerProperty->PropertyName</label>

									{# select type #}
									{% if ( $_customerProperty->PropertyType == 'select' ) %}
									<select id="plentyCustomerProperty-$_customerProperty->PropertyID" class="form-control" data-plenty-property-id="$_customerProperty->PropertyID">
										{% for $_customerPropertyValue in GetCheckoutCustomerPropertyValuesList($_customerProperty->PropertyID) %}
											<option value="$_customerPropertyValue->PropertyValueID">$_customerPropertyValue->PropertyValueName</option>
										{% endfor %}
									</select>
									{% elseif ( $_customerProperty->PropertyType == 'number' ) %}
										<input id="plentyCustomerProperty-$_customerProperty->PropertyID" class="form-control" type="text" data-plenty-property-id="$_customerProperty->PropertyID" value="$_customerProperty->PropertyValue" placeholder="$_customerProperty->PropertyName">
									{% elseif ( $_customerProperty->PropertyType == 'customerType' ) %}
										<input id="plentyCustomerProperty-$_customerProperty->PropertyID" class="form-control" type="text" data-plenty-property-id="$_customerProperty->PropertyID" value="$_customerProperty->PropertyValue" placeholder="$_customerProperty->PropertyName">
									{% elseif ( $_customerProperty->PropertyType == 'date' ) %}
										<input id="plentyCustomerProperty-$_customerProperty->PropertyID" class="form-control" type="text" data-plenty-property-id="$_customerProperty->PropertyID" value="$_customerProperty->PropertyValue" placeholder="$_customerProperty->PropertyName">
										<script>
											jQuery(function(){
												jQuery('#plentyCustomerProperty-$_customerProperty->PropertyID').datepicker();
											});
										</script>
									{% elseif ( $_customerProperty->PropertyType == 'longText' ) %}
										<textarea id="plentyCustomerProperty-$_customerProperty->PropertyID" class="form-control" data-plenty-property-id="$_customerProperty->PropertyID" placeholder="$_customerProperty->PropertyName" style="height: 106px;">$_customerProperty->PropertyValue</textarea>
									{% else %}
										{% GetCheckoutCustomerPropertiesInput($_customerProperty->PropertyID, '') %}
									{% endif %}

								</div>
							</div>
						{% endfor %} 
					</div>
				</div>

			</div>
		{% endif %}

		<div class="margin-top-2 text-right">
			<button class="btn btn-primary btn-lg btn-block-xs" type="submit"><span class="glyphicon glyphicon-ok" aria-hidden="true"></span> Registrierung abschließen</button>
		</div>

	</form>

</div>

<script>
(function($) {
	$(document).ready(function() {

		// visibility state of additional fields
		var registrationAdditionalOpened = {% if $_additionalFieldsVisible == false %}false{% else %}true{% endif %};
		$('[data-plenty-checkout-form="guestRegistration"]').find('[data-plenty^="UI.initSlideToggle"][data-plenty-rel^="#additional"]').click(function() {
			if ( ! $(this).is('.disabled') ) {
				if ( registrationAdditionalOpened == false ) {
					registrationAdditionalOpened = true;
				}
				else {
					registrationAdditionalOpened = false;
				}
			}
		});

		// functions open/close additional fields if hidden field is set/unset mandatory
		$.fn.openRegistrationAdditional = function() {
			this.closest('form').find('[data-plenty^="UI.initSlideToggle"][data-plenty-rel^="#additional"]').each(function() {
				$(this).addClass('disabled');
				if ( ! $( $(this).attr('data-plenty-rel') ).is(':visible') ) {
					$(this).click();
				}
			});
		}
		$.fn.closeRegistrationAdditional = function() {
			this.closest('form').find('[data-plenty^="UI.initSlideToggle"][data-plenty-rel^="#additional"]').each(function() {
				if ( $( $(this).attr('data-plenty-rel') ).is(':visible') && $( $(this).attr('data-plenty-rel') ).find('[data-plenty-validate]').length < 1 ) {
					if ( registrationAdditionalOpened == false ) {
						$(this).click();
						registrationAdditionalOpened = false;
					}
					$(this).removeClass('disabled');
				}
			});
		}

		// set mandatory fields dependent on form of address
		$('#registrationFormOfAddress').on('change', function() {

			// form of address is "company"
			if ( $('#registrationFormOfAddress').val() == 2 ) {

				{% if GetGlobal("ValidateInvoiceAddressCompanyName") == false %}
					// enable "company name", mark mandatory
					$('#registrationCompany').removeAttr('disabled').closest('.form-group').removeClass('disabled').attr('data-plenty-validate', 'text');
				{% endif %}

				{% if GetGlobal("ValidateInvoiceAddressFirstNameIfCompany") == false %}
					// mark "first name" as optional
					$('#registrationFirstName').closest('.form-group').removeClass('has-error').removeAttr('data-plenty-validate');
				{% endif %}

				{% if GetGlobal("ValidateInvoiceAddressLastNameIfCompany") == false %}
					// mark "last name" as optional
					$('#registrationLastName').closest('.form-group').removeClass('has-error').removeAttr('data-plenty-validate');
				{% endif %}

				{% if GetGlobal("ValidateInvoiceAddressCompanyName") == false && GetGlobal("ValidateInvoiceAddressVatIfCompany") == true %}
					// mark "vat" as mandatory
					$('#registrationVatNumber').closest('.form-group').attr('data-plenty-validate', 'text');
					// open additional fields
					$('#registrationVatNumber').openRegistrationAdditional();
				{% endif %}

			// form of address is not "company"
			} else {

				{% if GetGlobal("ValidateInvoiceAddressCompanyName") == false %}
					// disable "company name", mark as optional
					$('#registrationCompany').val('').attr('disabled','disabled').closest('.form-group').removeClass('has-error').addClass('disabled').removeAttr('data-plenty-validate');
				{% endif %}

				{% if GetGlobal("ValidateInvoiceAddressFirstName") == true %}
					// mark "first name" as mandatory
					$('#registrationFirstName').closest('.form-group').attr('data-plenty-validate', 'text');
				{% endif %}

				{% if GetGlobal("ValidateInvoiceAddressLastName") == true %}
					// mark "last name" as mandatory
					$('#registrationLastName').closest('.form-group').attr('data-plenty-validate', 'text');
				{% endif %}

				{% if GetGlobal("ValidateInvoiceAddressCompanyName") == false && GetGlobal("ValidateInvoiceAddressVatIfCompany") == true %}
					// mark "vat" as not mandatory
					$('#registrationVatNumber').closest('.form-group').removeClass('has-error').removeAttr('data-plenty-validate');
					// close additional fields
					$('#registrationVatNumber').closeRegistrationAdditional();
				{% endif %}

				{% if GetGlobal("ValidateInvoiceAddressFirstNameIfFamily") == false %}
				// is "family"
				if ( $('#registrationFormOfAddress').val() == 3 ) {
					// mark "first name" as optional
					$('#registrationFirstName').closest('.form-group').removeAttr('data-plenty-validate');
				}
				{% endif %}

			}

		});

		// set mandatory fields dependent on country
		var storeCountryID = $StoreCountryID;
		$('#registrationCountry').on('change', function() {

			var currentStoreCountryID = $('#registrationCountry').val();

			// country is foreign country
			if ( currentStoreCountryID != storeCountryID ) {

				{% if ( GetGlobal("ValidateInvoiceAddressPhoneNumber") == false && GetGlobal("ValidateInvoiceAddressPhoneNumberIfForeignCustomer") == true ) %}
					$('#registrationPhone').closest('.form-group').attr('data-plenty-validate', 'text');
				{% endif %}

				{% if ( GetGlobal("ValidateInvoiceAddressFaxNumber") == false && GetGlobal("ValidateInvoiceAddressFaxNumberIfForeignCustomer") == true ) %}
					$('#registrationFax').closest('.form-group').attr('data-plenty-validate', 'text');
				{% endif %}

				{% if ( GetGlobal("ValidateInvoiceAddressMobileNumber") == false && GetGlobal("ValidateInvoiceAddressMobileNumberIfForeignCustomer") == true ) %}
					$('#registrationMobile').closest('.form-group').attr('data-plenty-validate', 'text');
				{% endif %}

				// open additional fields
				{% 
					if ( 
